CREATE TABLE zip_codes(
id INT PRIMARY KEY,
sid INT,
default_state CHAR(2),
city VARCHAR(50),
default_type VARCHAR(10),
zip_code CHAR(5),
growing_zone INTEGER
);


CREATE TABLE trees(
   ID                  INTEGER  NOT NULL PRIMARY KEY 
  ,growing_zone        INTEGER 
  ,tree_name           VARCHAR(30)
  ,botanical_name      VARCHAR(45)
  ,descrip1            VARCHAR(47)
  ,descrip2            VARCHAR(51)
  ,descrip3            VARCHAR(47)
  ,descrip4            VARCHAR(64)
  ,descrip5            VARCHAR(61)
  ,descrip6            VARCHAR(25)
  ,grows_in_zones      VARCHAR(4)
  ,sun_preference      VARCHAR(23)
  ,soil_preference     VARCHAR(16)
  ,mature_height       VARCHAR(12)
  ,mature_spread       VARCHAR(12)
  ,growth_speed        VARCHAR(6)
  ,flower_color        VARCHAR(11)
  ,foliage_color       VARCHAR(11)
  ,fall_color          VARCHAR(30)
  ,pollinator_friendly VARCHAR(3)
  ,outgoing_link       VARCHAR(58)
  ,main_tree_image     VARCHAR(142)
  ,tree_image_1        VARCHAR(134)
  ,tree_image_2        VARCHAR(136)
  ,tree_image_3        VARCHAR(137)
  ,tree_image_4        VARCHAR(134)
);
